package core_java_patternsprograms;

public class PatternStarpogm {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int i,j, n=7;  
		   System.out.println("Right angle triangle");  
		   for(i=1;i<n;i++)  
		   {  
		   for(j=1;j<=i;j++)  
		   {  
		       System.out.print(" *");  
		   }  
		       System.out.println("");  
	}
	}

}
